contentCss:
body {
  font-family: 'Roboto Condensed', sans-serif;
  line-height: 1.4;
  margin: 1rem;
  color: #343a40;
  font-size:16px;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

::-webkit-scrollbar {
   width: 7px;
   max-height: 5px;
}

::-webkit-scrollbar-track {
   background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
   background: #B4C1D1;
}

::-webkit-scrollbar-thumb:hover {
   background: #80A0C5;
}

initConfig:
tinymce.init({
  plugins: [
    "a11ychecker advcode casechange formatpainter",
    "linkchecker autolink lists checklist",
    "media mediaembed pageembed permanentpen",
    "powerpaste table advtable tinymcespellchecker"
  ],
  toolbar: "formatselect | fontselect | bold italic strikethrough forecolor backcolor formatpainter | alignleft aligncenter alignright alignjustify | numlist bullist outdent indent | link insertfile image | removeformat | code | addcomment | checklist | casechange",
  height: 360
});

lessVariables:
@background-color: #fff;
@toolbar-background-color: @background-color;
@menubar-background-color: @background-color;
@border-color: rgba(224, 224, 224, 1);
@color-black: rgba(52, 58, 64, 1);
@color-white: #fff;
@color-tint: #207ab7;
@color-error: #c00;
@color-success: #78AB46;
@base-value: 16px;
@control-border-radius: 4px;
@panel-border-radius: 4px;
@tinymce-border-radius: 4px;
@font-stack: Roboto;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-text-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-hover-background-color: multiply(white, contrast(@background-color, fade(@color-black, 15), fade(@color-black, 85)));
@toolbar-button-hover-icon-color: @toolbar-button-icon-color;
@toolbar-button-hover-text-color: @toolbar-button-icon-color;
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
